mapper.insert:
insert into TT_SPU (ID, SPU_NAME, SPU_CODE, SPU_TYPE,SPU_DOC,SPU_DESC, SPU_EN_DESC, DETAIL,
CATEGORY_CODE, BRAND_CODE)
values (#{id},#{spuName}, #{spuCode}, #{spuType},#{spuDoc},  #{spuDesc}, #{spuEnDesc},
#{detail,jdbcType=VARCHAR}, #{categoryCode,jdbcType=VARCHAR}, #{brandCode,jdbcType=VARCHAR}
)

mapper.update:
update TR_SPU_ATTR
set ATTR_ID = #{attrId},
SORT_ORDER = #{sortOrder}
where ID = #{id} AND SPU_CODE = #{spuCode}

mapper.select:
select

from TT_SPU
WHERE CATEGORY_CODE = #{categoryCode} AND BRAND_CODE = #{brandCode}

mapper.update:
update TR_SPU_COMPONENT
set SUB_SPU_CODE = #{subSpuCode},
SUB_NUM = #{subNum}
where ID = #{id} AND SPU_CODE = #{spuCode}

update TR_SPU_DESIGNER
set DESIGNER_ID = #{designerId}
where ID = #{id} AND SPU_CODE = #{spuCode}
