mapper.select:
select ts.ID, ts.SPU_NAME, ts.SPU_CODE, ts.DESCRIPTION, ts.DETAIL, ts.CATEGORY_CODE, ts.BRAND_CODE,
tc.CATEGORY_NAME, tb.BRAND_NAME
from TT_SPU ts LEFT JOIN TT_CATEGORY tc ON tc.CATEGORY_CODE=ts.CATEGORY_CODE LEFT JOIN TT_BRAND tb ON tb.BRAND_CODE = ts.BRAND_CODE where ts.SPU_CODE = #{spuCode}

mapper.insert:
insert into TT_SPU (ID, SPU_NAME, SPU_CODE, DESCRIPTION,
DETAIL, CATEGORY_CODE, BRAND_CODE
)
values (#{id},#{spuName,jdbcType=VARCHAR}, #{spuCode,jdbcType=VARCHAR}, #{description,jdbcType=VARCHAR},
#{detail,jdbcType=VARCHAR}, #{categoryCode,jdbcType=VARCHAR}, #{brandCode,jdbcType=VARCHAR}
)

mapper.select:
select

from TT_SPU
WHERE CATEGORY_CODE = #{categoryCode} AND BRAND_CODE = #{brandCode}
