mapper.select:
select 

from TR_PROJECT_IMAGE
where ID = #{id,jdbcType=BIGINT}
  and PROJECT_ID = #{projectId,jdbcType=BIGINT}
  and IMAGE_URL = #{imageUrl,jdbcType=VARCHAR}

mapper.delete:
delete from TR_PROJECT_IMAGE
where ID = #{id,jdbcType=BIGINT}
  and PROJECT_ID = #{projectId,jdbcType=BIGINT}
  and IMAGE_URL = #{imageUrl,jdbcType=VARCHAR}

mapper.update:
update TR_PROJECT_IMAGE


    IMAGE_DESC = #{imageDesc,jdbcType=VARCHAR},


    IS_MAIN = #{isMain,jdbcType=INTEGER},


where ID = #{id,jdbcType=BIGINT}
  and PROJECT_ID = #{projectId,jdbcType=BIGINT}
  and IMAGE_URL = #{imageUrl,jdbcType=VARCHAR}

update TR_PROJECT_IMAGE
set IMAGE_DESC = #{imageDesc,jdbcType=VARCHAR},
  IS_MAIN = #{isMain,jdbcType=INTEGER}
where ID = #{id,jdbcType=BIGINT}
  and PROJECT_ID = #{projectId,jdbcType=BIGINT}
  and IMAGE_URL = #{imageUrl,jdbcType=VARCHAR}
